package Java.Aula_02;

/**
 * 
 */
public class Aula_02F {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int idade = 30;        // variável numérica inteira
		double altura = 1.75;  // variável numérica de ponto flutuante
		String nome = "João";  // variável de texto
		boolean ativo = true;  // variável lógica
		char letra = 'A';      // variável de caractere
		
		System.out.printf("Nome: %s, Idade: %d, Altura: %.2f, Letra: %c, Ativo: %b%n", 
                nome, idade, altura, letra, ativo);		
	}
}
